
window.onload=()=>{const noteArr=[];button=document.querySelector('.submit')
button.onclick=function(){newInput=document.querySelector('.input').value;notePad=document.querySelector('.list');noteArr.push(newInput);console.log(noteArr);let task=document.createElement('li');task.setAttribute('style','display:flex;border:2px solid black;justify-content:space-between;margin-bottom:5px');let li=document.createElement('span');for(i=0;i<noteArr.length;i++){li.innerHTML=noteArr[i];task.appendChild(li);}
let dateToday=new Date()
let date=document.createElement('span');date.innerHTML=`${dateToday.getFullYear()} ${dateToday.getMonth()+1} ${dateToday.getDate()}`
task.appendChild(date);notePad.appendChild(task)
if(noteArr.length>5){notePad.removeChild(notePad.firstElementChild);noteArr.splice(1,1);}
document.querySelector('.input').value=null;}
let newElement=document.createElement('div');let newDiv=new DocumentFragment();let images=[{imgPath:'img/image.png',alt:'png image',href:'https://prog.academy/javascript'},{imgPath:'img/image1.png',alt:'png image',href:'https://prog.academy/fullstack'}]
for(const iterator of images){const{imgPath,alt,href}=iterator;div=document.createElement('div')
div.innerHTML=`<a href="${href}" target='blank'><img src="${imgPath}" alt="${alt}"></a>`;newDiv.appendChild(div)
newElement.appendChild(newDiv)}
document.body.appendChild(newElement)
let xArr=['','A','B','C','D','E','F','G','H','i','J'];let field=document.querySelector('.field')
field.setAttribute('style','width:440px;height:440px;border:1px solid black')
function canvas(arr,field,count){for(t=0;t<=count;t++){let xBand=document.createElement('div');xBand.setAttribute('style','display:flex;')
for(const i of arr){cell=document.createElement('div');cell.setAttribute('style','display:flex;justify-content:center;align-items:center;width:40px;height:40px;border:1px solid black')
if(t==0){cell.innerHTML=i;}
if(t>0&&i==''){cell.innerHTML=t;}
xBand.appendChild(cell);}
field.appendChild(xBand)}
document.body.appendChild(field);}
canvas(xArr,field,10)}